* Github: https://github.com/fbog05/
* Licenc: GNU GPL
*/

import { StatusBar } from 'expo-status-bar';
import { useState } from 'react';
import { FlatList, Pressable, StyleSheet, Text, View } from 'react-native';

export default function App() {

  let host = "http://localhost:3000/"
  let endpoint = "orszagok"
  let url = host + endpoint

  const [orszagok, setOrszagok] = useState([]);

  function getOrszagok() {

    fetch(url)
      .then(res => res.json())
      .then(res => {
        console.log(res);
        setOrszagok(res);
      });
  }

  return (
    <View style={styles.container}>
      <Text style={styles.cim}>Országok</Text>
      <Pressable
        onPress={getOrszagok}
        style={styles.button}
      >
        <Text style={styles.buttonText}>Lekér</Text>
      </Pressable>

      <FlatList
        data={orszagok}
        renderItem={({ item }) => (
          <View style={styles.item}>
            <Text style={styles.szoveg}>{item.nev}</Text>
            <Text style={styles.szoveg}>{item.terulet}</Text>
            <Text style={styles.szoveg}>{item.nepesseg}</Text>
            <Text style={styles.szoveg}>{item.fovaros}</Text>
          </View>
        )}
        ListHeaderComponent={()=> <Text>Név Népesség</Text>}
      />

      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'lightpink',
    alignItems: 'center',
    justifyContent: 'center',
  },

  cim: {
    color: '#800040',
    fontWeight: "800",
    fontSize: 25,
    marginTop: 35,
    paddingBottom: 20,
  },

  button: {
    backgroundColor: '#800040',
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 10,
    marginBottom: 40,
    shadowColor: '#171717',
    shadowOffset: { width: 5, height: 5 },
    shadowOpacity: 0.2,
    shadowRadius: 3,
  },

  buttonText: {
    color: 'white',
    fontSize: 14,
    fontWeight: "700",
  },

  item: {
    border: 'solid 2px #800040',
    margin: 3,
    padding: 3,
    borderRadius: 3,
    boxShadow: '3px 3px 3px #800040',
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'space-between',
  },

  szoveg: {
    color: '#800040',
    fontWeight: "700",
    padding: 5,
    fontSize: 18,
  },
});
